#!/bin/bash
whois $1 | awk -F': ' 'BEGIN{split("Registrant Organization,Registrant State/Province,Registrant Country,Registrant Email,Admin Organization,Admin State/Province,Admin Country,Admin Email,Tech Organization,Tech State/Province,Tech Country,Tech Email",f,",");for(i in f)v[f[i]]=""} /^(Registrant|Admin|Tech) (Organization|State\/Province|Country|Email):/ {gsub(/^[ \t]+|[ \t]+$/,"",$2);v[$1]=$2} END{for(i=1;i<=4;i++)v[f[i]]=v[f[i]];for(i=5;i<=8;i++)if(v[f[i]]=="")v[f[i]]=v[f[i-4]];for(i=9;i<=12;i++)if(v[f[i]]=="")v[f[i]]=v[f[i-8]];for(i=1;i<=length(f);i++)print f[i]","v[f[i]]}' > $1.csv
